mapper.insert:

insert into tb_class (id, cla_name, grade_id, 
  introducation, gmt_create, gmt_modify,cla_teacher
  )
values (#{id,jdbcType=INTEGER}, #{claName,jdbcType=VARCHAR}, #{gradeId,jdbcType=INTEGER}, 
  #{introducation,jdbcType=VARCHAR}, #{gmtCreate,jdbcType=TIMESTAMP}, #{gmtModify,jdbcType=TIMESTAMP},#{claTeacher,jdbcType=VARCHAR}
  )

mapper.select:
SELECT *
FROM tb_class
WHERE cla_name =#{0}and grade_id = #{1}

SELECT *
FROM tb_class
WHERE grade_id=#{0}   ORDER BY id DESC limit #{1},#{2}

SELECT count(1)
FROM tb_class
WHERE grade_id =#{0}
